(function($){$.fn.smoothyScrolly=function(options){var selector=this;var settings=$.extend({'height':0,'speed':800,'class':'active'},options);this.find('a').on('click',function(event){if(this.hash!==""){event.preventDefault();var hash=this.hash;$('html, body').animate({scrollTop:$(hash).offset().top-settings.height},settings.speed)}});this.find('a').each(function(){var anchorHash=$(this).prop('hash');$(document).on('scroll',function(){if($(this).scrollTop()>=$(anchorHash).position().top-settings.height){$(selector).find('a').parent().removeClass(settings.class);$(selector).find('a[href="'+anchorHash+'"]').parent().addClass(settings.class)}})});return this}}(jQuery))