jQuery(document).ready(function($){$('.ia-smooth-scroll').find('a').on('click',function(event){if(this.hash!==""){event.preventDefault();var hash=this.hash;$('html, body').animate({scrollTop:$(hash).offset().top-50},800)}});$('.ia-smooth-scroll a').each(function(){var anchorHash=$(this).prop('hash');$(document).on('scroll',function(){if($(this).scrollTop()>=$(anchorHash).position().top-50){$('.ia-smooth-scroll').find('a').parent().removeClass('active');$('.ia-smooth-scroll').find('a[href='+anchorHash+']').parent().addClass('active')}})})})